    "Parameterless"      !! "" !
                                           DateTime.parse("2014-01-28T19:04:14.469+00:00") ! toNameValuePairs() ! "localhost".some  ! "10.0.2.2".some  ! "Mozilla/5.0 (Macintosh; Intel Mac OS X 10.9; rv:26.0) Gecko/20100101 Firefox/26.0".some ! None               ! List("Cache-Control: max-age=0", "Connection: keep-alive", "Cookie: __utma=111872281.878084487.1390237107.1390848487.1390931521.6; __utmz=111872281.1390237107.1.1.utmcsr=(direct)|utmccn=(direct)|utmcmd=(none); _sp_id.1fff=b89a6fa631eefac2.1390237107.6.1390931545.1390848641; hblid=CPjjuhvF05zktP7J7M5Vo3NIGPLJy1SF; olfsk=olfsk562923635617554; __utmc=111872281; wcsid=uMlog1QJVD7juhFZ7M5VoBCyPPyiBySS; _oklv=1390931585445%2CuMlog1QJVD7juhFZ7M5VoBCyPPyiBySS; _ok=9752-503-10-5227; _okbk=cd4%3Dtrue%2Cvi5%3D0%2Cvi4%3D1390931521123%2Cvi3%3Dactive%2Cvi2%3Dfalse%2Cvi1%3Dfalse%2Ccd8%3Dchat%2Ccd6%3D0%2Ccd5%3Daway%2Ccd3%3Dfalse%2Ccd2%3D0%2Ccd1%3D0%2C; sp=75a13583-5c99-40e3-81fc-541084dfc784", "Accept-Encoding: gzip, deflate", "Accept-Language: en-US, en", "Accept: text/html, application/xhtml+xml, application/xml;q=0.9, */*;q=0.8", "User-Agent: Mozilla/5.0 (Macintosh; Intel Mac OS X 10.9; rv:26.0) Gecko/20100101 Firefox/26.0", "Host: localhost:4001") ! "75a13583-5c99-40e3-81fc-541084dfc784".some |> {

      (_, raw, timestamp, payload, hostname, ipAddress, userAgent, refererUri, headers, userId) => {

        val canonicalEvent = ThriftLoader
          .toCollectorPayload(Base64.decodeBase64(raw))

        val expected = CollectorPayload(
          api          = Expected.api,
          querystring  = payload,
          body         = None,
          contentType  = None,
          source       = CollectorSource(Expected.collector, Expected.encoding, hostname),
          context      = CollectorContext(timestamp.some, ipAddress, userAgent, refererUri, headers, userId)
          )

        canonicalEvent must beSuccessful(expected.some)
      }
    }

  // A bit of fun: the chances of generating a valid Thrift CollectorPayload at random are
  // so low that we can just use ScalaCheck here
  def e2 =
    check { (raw: String) => ThriftLoader.toCollectorPayload(Base64.decodeBase64(raw)) must beFailing }

}
